import React from "react";
import Square from "./Square";
import "./Board.css";

class Board extends React.Component {
  renderSquare(i) {
    return (
      <Square
        value={this.props.squares[i]}
        onClick={() => this.props.onClick(i)}
      />
    );
  }

  render() {
    const squers = [];
    for (let i = 0; i < this.props.squares.length; i + 3) {
      const row = [];
      for (let j = 0; j < this.props.squares.length; j + 3) {
        row.push(this.renderSquare(j));
        row.push(this.renderSquare(j + 1));
        row.push(this.renderSquare(j + 2));
      }
      squers.push(
        <div key={i} className="board-row">
          {row}
        </div>
      );
    }

    // for (let i = 0; i < this.props.squares.length; i++) {
    //   this.renderSquare(i);
    // }

    return <div>{squers}</div>;
  }
}

export default Board;
