import React from "react";
import Board from "./Board";
import "./Game.css";

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      history: [
        {
          squares: Array(9).fill(null),
        },
      ],
      place: [null, null],
      stepNumber: 0,
      xIsNext: true,
    };
  }

  handleClick(i) {
    const history = this.state.history.slice(0, this.state.stepNumber + 1);
    const current = history[history.length - 1];
    const squares = current.squares.slice();

    if (this.props.calculateWinner(squares) || squares[i]) {
      return;
    }
    squares[i] = this.state.xIsNext ? "X" : "O";
    this.setState({
      history: history.concat([
        {
          squares: squares,
        },
      ]),
      stepNumber: history.length,
      xIsNext: !this.state.xIsNext,
    });
  }

  jumpTo(step) {
    this.setState({
      stepNumber: step,
      xIsNext: step % 2 === 0,
    });
  }

  location(squares, place) {
    for (let i = 0; i < squares.length; i++) {
      if (squares[i]) {
        if (i < 3) {
          place[0] = 0;
          place[1] = i;
        } else if (i < 6) {
          place[0] = 0;
          place[1] = i-3;
        } else {
          place[0] = 0;
          place[1] = i-6;
        }
      }
    }
    return place;
  }

  render() {
    const place = [null, null];
    const history = this.state.history;
    const current = history[this.state.stepNumber];
    const winner = this.props.calculateWinner(current.squares);
    

    const moves = history.map((step, move) => {
      const point = this.location(step.squares, place);
      const desc = move
        ? "Go to move #" + move + " at " + point
        : "Go to game start";
      return (
        <li key={move}>
          <button onClick={() => this.jumpTo(move)}>{desc}</button>
        </li>
      );
    });

    let status;
    if (winner) {
      status = "Winner: " + winner;
    } else {
      status = "Next player: " + (this.state.xIsNext ? "X" : "O");
    }

    return (
      <div className="game">
        <div className="game-board">
          <Board
            squares={current.squares}
            onClick={(i) => this.handleClick(i)}
          />
        </div>
        <div className="game-info">
          <div>{status}</div>
          <ol>{moves}</ol>
        </div>
      </div>
    );
  }
}

export default Game;
